import React, { useEffect, useState } from 'react'
import styles from "../style";
import {Navbar, Identitas, Formprofile, Instruction} from "../components/profile/magang"
import { useLocation } from 'react-router-dom';

const Profilemaba = (loggedUser) => {
  const [user, setUser] = useState(null)

  const location = useLocation();
  const state = location.state;

  const id = localStorage.getItem('id');

  useEffect(() => {
    // Mengambil data dari API
    fetch('http://103.127.135.153:5000/api/user/'+id)
      .then(async response => response.json())
      .then(data => setUser(data));
  }, []);

  useEffect(()=>{
    if(loggedUser != null){
      setUser(loggedUser);
    }
  },[user])
  return (
    <>
    <div className=" pb-20">
        <Navbar nama={user[0].nama_siswa}/>
    </div>

    <div className="flex flex-row px-20">
        <Identitas />
        <div className='px-6'>
          <Formprofile />
        </div>
        <div className='flex flex-col'>

            <Instruction />
          </div>
    </div>

      <div>
          <div className={`${styles.flexCenter} py-16 `}>

          </div>
      </div>

    </>
  )
}

export default Profilemaba
