import React, { useEffect, useState } from 'react'
import styles from "../style";
import { Navbar, Identitas, Formprofile, Instruction } from "../components/profile/magang"
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
import Loading from '../components/loading';

const Profilemaba = (loggedUser) => {
  let refreshToken = Cookies.get('refresh_token')
  const location = useLocation();
  const [user, setUser] = useState('')
  const state = location.state;

  const id = localStorage.getItem('id');
  useEffect(() => {
    // setUser(data);
    if (!refreshToken) {
      localStorage.clear()
    } else if (refreshToken) {
      let name = localStorage.getItem('nama')
      setUser(name)
    }
  }, [user]);

  useEffect( () => {
   async function fetchData(){
    try {
      const response = await axios.get('http://103.127.135.153:5000/api/user/' + id);
      setUser(response.data[0]);

    } catch (error) {
      console.error('Error fetching user data:', error);
    }
    console.log(user)
   }
   fetchData();
  }, [])

  // useEffect(()=>{
  //   if(loggedUser != null){
  //     setUser(loggedUser);
  //   }
  // },[user])

  return (
    <>
      {user == '' ? <Loading type={'balls'} color={"#aaaaaa"}/> : <><div className=" pb-20">
        <Navbar nama={'user'} />
      </div><div className="flex flex-row px-20">
          <Identitas />
          <div className='px-6'>
            <Formprofile />
          </div>
          <div className='flex flex-col'>

            <Instruction />
          </div>
        </div><div>
          <div className={`${styles.flexCenter} py-16 `}>

          </div>
        </div></>}
    </>
  )
}

export default Profilemaba
